# Rosalind 18, Hamming Distance Problem

def hamming_distance(sequence1, sequence2):
    distance = 0 
    
    for i in range(len(sequence1)):
        if sequence1[i] != sequence2[i]: # check if positions match
            distance += 1 
            
    return distance


sequence1 = ""

sequence2 = ""

result = hamming_distance(sequence1, sequence2)
print(result)