# Rosalind 20, Approximate Pattern Matching Problem

# from rosalind 18, hamming distance function 
def hamming_distance(sequence1, sequence2):
    distance = 0 
    
    for i in range(len(sequence1)):
        if sequence1[i] != sequence2[i]: # check if positions match
            distance += 1 
            
    return distance


def approx_pattern_match(pattern, text, d):
    postitions = [] # to hold starting positions
    k = len(pattern)
    
    for i in range(len(text) - k + 1): # sliding window
        window = text[i:i+k] # take current window of length k 
        
        if hamming_distance(pattern, window) <= d: # check if mismatch are <= d
            postitions.append(i)
            
    return postitions

pattern = "GTAAGCATATA"
text = ""
d = 4

positions = approx_pattern_match(pattern, text, d)
print(*positions)